import './common.scss';
import { Map, GoogleApiWrapper,Marker } from 'google-maps-react';

const MAP_KEY = 'AIzaSyCtwgyBDAYf6RC9w9l91HNR3Y6-dVVcTts';
const HOTEL_MARK = {lat:34.975008,lng:138.386710};
const mapStyles = {
  width: '100%',
